                                        <div
                                            class="block px-4 py-2 text-xs text-gray-400"
                                        >
                                            {{ __("Switch Teams") }}
                                        </div>

                                        <template
                                            v-for="team in $page.user.all_teams"
                                        >
                                            <form
                                                @submit.prevent="
